import React, { Component } from 'react'

class App extends Component {
  constructor() {
    super()
    this.state = {
      name: 'This name will change in 3 seconds.'
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({ name: 'The name has changed.' })
    }, 3000)
  }

  render() {
    return (<div>
      <p>{this.state.name}</p>
    </div>)
  }
}

export default App;

